$(document).ready(function(){const e=document.getElementById("vid");e.volume=.05,e.oncanplay=(()=>{e.play()}),e.onended=(()=>{$("#pause_resume_button").html("restart video")}),$("#pause_resume_button").on("click",()=>{e.paused?e.play():e.pause(),$("#pause_resume_button").html(e.paused?"unpause video":"pause video")}),$("#mute_unmute_button").on("click",()=>{e.muted=!e.muted,$("#mute_unmute_button").html(e.muted?"unmute video":"mute video")})});

